import { Component, signal, computed, type OnInit } from "@angular/core";
import { CommonModule } from "@angular/common";
import { ActivatedRoute, Router } from "@angular/router";
import { MatCardModule } from "@angular/material/card";
import { MatButtonModule } from "@angular/material/button";
import { MatIconModule } from "@angular/material/icon";
import { MatChipsModule } from "@angular/material/chips";
import { DashboardService } from "../../services/dashboard.service";
import { Candidate } from "../../models/candidate.model";

@Component({
  selector: "app-candidate-detail-page",
  standalone: true,
  imports: [
    CommonModule,
    MatCardModule,
    MatButtonModule,
    MatIconModule,
    MatChipsModule,
  ],
  templateUrl: "./candidate-detail-page.component.html",
  styleUrls: ["./candidate-detail-page.component.scss"],
})
export class CandidateDetailPageComponent implements OnInit {
  candidateSignal = signal<Candidate | null>(null);

  // Computed signal for days since application
  daysSinceApplication = computed(() => {
    const candidate = this.candidateSignal();
    if (!candidate) return 0;
    const date = new Date(candidate.applicationDate);
    const today = new Date();
    const diffTime = Math.abs(today.getTime() - date.getTime());
    return Math.ceil(diffTime / (1000 * 60 * 60 * 24));
  });

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private dashboardService: DashboardService
  ) {}

  ngOnInit(): void {
    const candidateId = Number(this.route.snapshot.paramMap.get("id"));
    const candidate = this.dashboardService
      .candidates()
      .find((c) => c.id === candidateId);

    if (candidate) {
      this.candidateSignal.set(candidate);
    } else {
      // Redirect to candidates page if candidate not found
      this.router.navigate(["/candidates"]);
    }
  }

  goBack(): void {
    this.router.navigate(["/candidates"]);
  }

  getStatusColor(status: string): string {
    switch (status) {
      case "Approved":
        return "primary";
      case "Rejected":
        return "warn";
      case "Under Review":
        return "accent";
      default:
        return "";
    }
  }

  getStatusIcon(status: string): string {
    switch (status) {
      case "Approved":
        return "check_circle";
      case "Rejected":
        return "cancel";
      case "Under Review":
        return "schedule";
      case "Pending":
        return "hourglass_empty";
      default:
        return "help";
    }
  }

  updateCandidateStatus(newStatus: Candidate["status"]): void {
    const candidate = this.candidateSignal();
    if (candidate) {
      this.dashboardService.updateCandidateStatus(candidate.id, newStatus);
      this.candidateSignal.update((c) =>
        c ? { ...c, status: newStatus } : null
      );
    }
  }
}
<div class="candidate-detail-container" *ngIf="candidateSignal() as candidate">
  <div class="detail-header">
    <button mat-icon-button (click)="goBack()" class="back-button">
      <mat-icon>arrow_back</mat-icon>
    </button>
    <h1>Candidate Details</h1>
  </div>

  <mat-card class="candidate-detail-card">
    <mat-card-header>
      <div class="candidate-header">
        <img [src]="candidate.image" [alt]="candidate.name" class="candidate-avatar">
        <div class="candidate-basic">
          <h2>{{ candidate.name }}</h2>
          <p class="candidate-email">{{ candidate.email }}</p>
          <div class="candidate-location">
            <mat-icon>place</mat-icon>
            {{ candidate.city }} • Age {{ candidate.age }}
          </div>
        </div>
      </div>
    </mat-card-header>

    <mat-card-content>
      <div class="detail-grid">
        <div class="detail-section">
          <h3>Status</h3>
          <mat-chip 
            [color]="getStatusColor(candidate.status)" 
            selected>
            <mat-icon matChipAvatar>{{ getStatusIcon(candidate.status) }}</mat-icon>
            {{ candidate.status }}
          </mat-chip>
        </div>

        <div class="detail-section">
          <h3>Application Date</h3>
          <p>{{ candidate.applicationDate }}</p>
          <small>{{ daysSinceApplication() }} days ago</small>
        </div>

        <div class="detail-section full-width">
          <h3>Summary</h3>
          <p>{{ candidate.summary }}</p>
        </div>

        <div class="detail-section full-width">
          <h3>Hobbies & Interests</h3>
          <p>{{ candidate.hobbies }}</p>
        </div>
      </div>

      <div class="action-buttons">
        <button mat-raised-button color="primary" (click)="updateCandidateStatus('Approved')">
          <mat-icon>check_circle</mat-icon>
          Approve
        </button>
        <button mat-raised-button color="accent" (click)="updateCandidateStatus('Under Review')">
          <mat-icon>schedule</mat-icon>
          Under Review
        </button>
        <button mat-raised-button color="warn" (click)="updateCandidateStatus('Rejected')">
          <mat-icon>cancel</mat-icon>
          Reject
        </button>
      </div>
    </mat-card-content>
  </mat-card>
</div>

<div class="not-found" *ngIf="!candidateSignal()">
  <mat-card>
    <mat-card-content>
      <div class="not-found-content">
        <mat-icon class="not-found-icon">person_off</mat-icon>
        <h2>Candidate Not Found</h2>
        <p>The candidate you're looking for doesn't exist.</p>
        <button mat-raised-button color="primary" (click)="goBack()">
          Back to Candidates
        </button>
      </div>
    </mat-card-content>
  </mat-card>
</div>
